items.additional_css:
/* Top Header */
.page-header .panel.wrapper {
    background-color: #000 !important;
}

/* Links */
a,
a:visited,
a.action.remind,
a.action.remind:visited,
.bundle-options-container button.action.back {
    color: #25335f;
}

a:hover,
a.action.remind:hover .bundle-options-container button.action.back:hover {
    color: #222;
}

a:hover {
    text-decoration: none;
}

/* Buttons */
button.action.primary,
a.action.create.primary {
    color: #fff;
    background-color: #25335f;
    border-color: #25335f;
}

button.action.primary:hover,
button.action.primary:active,
button.action.primary:focus,
a.action.create.primary:hover {
    background-color: #222;
    border-color: #222;
}

/* Navigation */
.navigation .level0.active>.level-top,
.navigation .level0.has-active>.level-top,
.navigation .level0 .submenu .active>a,
.block-collapsible-nav .item.current a,
.block-collapsible-nav .item.current>strong.fotorama__thumb-border {
    border-color: #d9282f;
}

/* Breadcrumbs */
.breadcrumbs a,
.breadcrumbs a:visited {
    color: #25335f;
}

.breadcrumbs a:hover {
    color: #25335f;
}

/* My Account Dropdown Links */
.customer-welcome li a,
.customer-welcome li a:visited {
    color: #575757 !important;
}

/* Arrow Next To Logged-In Customer's Name */
.customer-welcome .action.switch:after,
.customer-welcome .action.switch:hover {
    color: #575757;
}

/* My Account Sidebar Links */
.block-collapsible-nav .item.current a,
.block-collapsible-nav .item.current>strong {
    color: #575757 !important;
    border-color: #25335f;
}

/* Language and Currency Switchers */
.page-header .switcher strong,
.page-footer .switcher strong,
.page-header .switcher .options .action.toggle,
.customer-welcome .action.switch:after,
.customer-welcome .action.switch:hover,
.page-footer .switcher .options .action.toggle {
    color: #fff !important;
}

/* Mini Cart Icon */
.minicart-wrapper .action.showcart:before {
    color: #575757 !important;
}

/* Mini Cart Items Count (Orange) */
.minicart-wrapper .action.showcart .counter.qty {
    background: #044124;
    color: #ffffff;
}

/* My Account Sidebar Links */
.block-collapsible-nav .item a,
.block-collapsible-nav .item>strong {
    color: #575757 !important;
}

/* Photo Gallery Outlines */
.fotorama__thumb-border {
    border-color: #000;
}

/* Swatch Borders */
.swatch-option.image:not(.disabled):hover,
.swatch-option.color:not(.disabled):hover,
.swatch-option.selected {
    outline: #000;
}

/* Shopping Cart Styles */
.cart.table-wrapper .wishlist.split.button>.action.split,
.cart.table-wrapper .wishlist.split.button>.action.toggle,
.gift-item-block .title,
.paypal-review-discount .block>.title strong,
.cart-discount .block>.title strong {
    color: #044124;
}

.gift-options .actions-toolbar .secondary .action-cancel {
    background-color: white !important;
    color: #575757 !important;
}

/* Checkout Styles  - Orange Progress Bars, etc */
.opc-progress-bar-item._active:before,
.opc-progress-bar-item._active>span:before {
    background: #000;
    border-color: #000;
}

.opc-progress-bar-item._active>span:after,
.opc-wrapper .shipping-address-item.selected-item {
    border-color: #000;
}

.opc-wrapper .shipping-address-item.selected-item:after {
    background: #000;
}

/* Page Builder Styles */
/* Page Builder Primary Buttons */
a.pagebuilder-button-primary,
button.pagebuilder-button-primary,
div.pagebuilder-button-primary {
    background: #044124;
    border: #044124;
    color: white;
}

a.pagebuilder-button-primary:hover,
button.pagebuilder-button-primary:hover,
div.pagebuilder-button-primary:hover {
    background: #0cb585;
    border: #0cb585;
    color: white;
    cursor: pointer;
}

/* Page Builder Buttons as Links */
a.pagebuilder-button-link,
button.pagebuilder-button-link,
div.pagebuilder-button-link {
    color: #25335f !important;
}

/* Page Builder Slider Dots */
.slick-dots li.slick-active button {
    background: #25335f !important;
}

/* Page Builder Slider Arrows */
.slick-prev:before,
.slick-next:before,
.slick-prev:after,
.slick-next:after {
    color: #25335f !important;
}
